-- v_cartwalls
create view v_cartwalls as
  select audio.md5 AS md5, archives.localpath AS path, audio.start_smpl AS start, audio.end_smpl AS end, cartsaudio.cart AS cart, cartsaudio.text AS text, cartwalls.name AS wall_name, cartwalls.description AS wall_desc, cartwalls.page AS page, cartsets.id AS cartset_id, cartsets.name AS cartset, cartsets.description AS cartset_desc, cartproperties.name AS prop_name, cartstyleprops.value AS prop_value
  FROM audio,
    cartsaudio,
    cartwalls,
    cartsets,
    cartstyles,
    cartstyleprops,
    cartproperties,
    archives
  WHERE (cartsaudio.audioid = audio.id)
    AND (cartsaudio.cartwallid = cartwalls.id)
    AND (cartwalls.cartsetid = cartsets.id)
    AND (cartsaudio.cartstyleid = cartstyles.id)
    AND (cartstyleprops.cartstyleid = cartstyles.id)
    AND (cartstyleprops.cartpropertyid = cartproperties.id)
    AND (audio.archive = archives.id)
  ORDER BY cartwalls.id,
    cartsaudio.cart,
    cartproperties.id
;

-- v_audio_track
create view v_audio_track as
  select audio.id AS id, audio.md5 AS md5, audio.title AS title, artists.name AS artist, albums.name AS album, archives.id AS archiveid, archives.name AS archive, audio.music_track AS track, audio.music_released AS released, audio.origin AS origin, audio.censor AS censor, audiodir.dirid AS dir, audio.length_smpl AS length_smpl, audio.start_smpl AS start_smpl, audio.end_smpl AS end_smpl, audio.intro_smpl AS intro_smpl, audio.extro_smpl AS extro_smpl, lifespans.data AS lifespan, audio.sustainer AS sustainer
  FROM audio,
    audioartists,
    artists,
    albums,
    archives,
    lifespans,
    audiodir,
    dir
  WHERE (audioartists.audioid = audio.id)
    AND (audioartists.artistid = artists.id)
    AND (audio.archive = archives.id)
    AND (audio.music_album = albums.id)
    AND (audio.lifespan = lifespans.id)
    AND (audiodir.audioid = audio.id)
    AND (audiodir.dirid = dir.id)
    AND (audio.type = 1)
  ORDER BY audio.md5
;

-- v_audio_jingles
create view v_audio_jingles as
  select audio.id AS id, audio.md5 AS md5, audio.title AS title, archives.id AS archiveid, archives.name AS archive, jinglepkgs.name AS pkg, jinglepkgs.enabled AS enabled, jingletypes.name AS type, dir.id AS dirid, dir.name AS dir, audio.length_smpl AS length_smpl, audio.start_smpl AS start_smpl, audio.end_smpl AS end_smpl, audio.intro_smpl AS intro_smpl, audio.extro_smpl AS extro_smpl, lifespans.data AS lifespan
  FROM audio,
    archives,
    audiojinglepkgs,
    jinglepkgs,
    jingletypes,
    audiodir,
    dir,
    lifespans
  WHERE (archives.id = audio.archive)
    AND (audio.type = 1)
    AND (jinglepkgs.id = audiojinglepkgs.jinglepkgid)
    AND (audiojinglepkgs.audioid = audio.id)
    AND (audiojinglepkgs.jingletypeid = jingletypes.id)
    AND (audiodir.audioid = audio.id)
    AND (audiodir.dirid = dir.id)
    AND (audio.lifespan = lifespans.id)
  ORDER BY audio.md5
;

-- v_audio_adverts
create view v_audio_adverts as
  select audio.id AS id, audio.md5 AS md5, audio.title AS title, archives.id AS archiveid, archives.name AS archive, dir.id AS dirid, dir.name AS dir, companies.name AS company, audio.advert_description AS description, audio.length_smpl AS length_smpl, audio.start_smpl AS start_smpl, audio.end_smpl AS end_smpl, audio.intro_smpl AS intro_smpl, audio.extro_smpl AS extro_smpl, lifespans.data AS lifespan, 'f'::character(1) AS sustainer
  FROM audio,
    archives,
    audiodir,
    dir,
    lifespans,
    companies
  WHERE (archives.id = audio.archive)
    AND (audio.type = 2)
    AND (audiodir.audioid = audio.id)
    AND (audiodir.dirid = dir.id)
    AND (audio.lifespan = lifespans.id)
    AND (audio.advert_company = companies.id)
  ORDER BY audio.md5
;


-- Special statements for postgres:post databases
CREATE OR REPLACE VIEW v_audio
    (id,md5,title,artist,album,archiveid,archive,track,released,
        length_smpl,start_smpl,end_smpl,intro_smpl,extro_smpl,lifespan,sustainer)
AS  
    SELECT id,md5,title,artist,album,archiveid,archive,track,released,
            length_smpl,start_smpl,end_smpl,intro_smpl,extro_smpl,lifespan,sustainer
    FROM v_audio_track
    
    UNION
    
    SELECT id,md5,title,'' AS artist,pkg AS album,archiveid,archive,
            '0' AS track,'0' AS released,
            length_smpl,start_smpl,end_smpl,intro_smpl,extro_smpl,lifespan,enabled AS sustainer
    FROM v_audio_jingles

    UNION

    SELECT id,md5,title,'' AS artist,company AS album,archiveid,archive,
            '0' AS track,'0' AS released,
            length_smpl,start_smpl,end_smpl,intro_smpl,extro_smpl,lifespan, sustainer
    FROM v_audio_adverts
ORDER BY id;

